import "@openzeppelin/contracts/access/Ownable.sol";
import "../ERC721Tradable.sol";

// todo
contract ResourceERC1151 is ERC721Tradable {

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    struct DNA {
        uint8 race;
        uint8 sex;
        // special skill id
        uint8 skill;
        uint8 hair;
        uint8 beard;
        // skin color
        uint8 skin;
        // face details (scars, face paint, frecles)
        uint8 face;
        uint8 eyes;
        uint8 mouth;
    }

    event DnaUpdated(uint256 indexed _tokenId, DNA _dna);

    mapping(uint256 => DNA) public dna;

    string private __baseURI = "https://www.winzeland.network/meta/resources/";

    constructor() ERC721Tradable("Winzeland: Resource", "Resource") {}

    function mint(address player, DNA memory _dna)
        public onlyRole(MINTER_ROLE)
        returns (uint256) 
    {
        uint256 newItemId = totalSupply();
        _mint(player, newItemId);

        _setDna(newItemId, _dna);

        return newItemId;
    }

    function _setDna(uint256 _tokenId, DNA memory _dna) internal {
        dna[_tokenId] = _dna;
        emit DnaUpdated(_tokenId, _dna);
    }

    function _baseURI() internal view override returns (string memory) {
        return __baseURI;
    }

    function setBaseUri(string memory uri) public onlyRole(DEFAULT_ADMIN_ROLE) {
        __baseURI = uri;
    }
}

contracts/ERC721Tradable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract OwnableDelegateProxy {}

/**
 * Used to delegate ownership of a contract to another address, to save on unneeded transactions to approve contract use for users
 */
contract ProxyRegistry {
    mapping(address => OwnableDelegateProxy) public proxies;
}

/**
 * @title ERC721Tradable
 * ERC721Tradable - ERC721 contract that whitelists a trading address
 */
abstract contract ERC721Tradable is ERC721Enumerable, AccessControlEnumerable {
    address public proxyRegistryAddress;

    event ProxyRegistryAddressChanged(address indexed _newAddress);

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    /**
     * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-less listings.
     */
    function isApprovedForAll(address _owner, address _operator)
        override
        public
        view
        returns (bool)
    {
        // Whitelist OpenSea proxy contract for easy trading.
        ProxyRegistry proxyRegistry = ProxyRegistry(proxyRegistryAddress);
        if (address(proxyRegistry.proxies(_owner)) == _operator) {
            return true;
        }

        return super.isApprovedForAll(_owner, _operator);
    }

    function setProxyRegistryAddress(address _proxyRegistryAddress) public onlyRole(DEFAULT_ADMIN_ROLE) {
        proxyRegistryAddress = _proxyRegistryAddress;
        emit ProxyRegistryAddressChanged(_proxyRegistryAddress);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Enumerable, AccessControlEnumerable) returns (bool) {
        return interfaceId == type(IERC721Enumerable).interfaceId
         || interfaceId == type(IAccessControlEnumerable).interfaceId
         || super.supportsInterface(interfaceId);
    }

    function owner() public view returns (address) {
        return getRoleMember(DEFAULT_ADMIN_ROLE, 0);
    }
}

