contracts/mixins/ERC1155TradableMixin.so.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/IERC1155MetadataURI.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "../extras/TradableProxyRegistry.sol";
import "./ContextMixin.sol";

/**
 * @title ERC1155TradableMixin
 * ERC1151TradableMixin - ERC1151 contract that whitelists a trading address
 */
abstract contract ERC1155TradableMixin is ERC1155Supply, AccessControlEnumerable, ContextMixin {
    address public proxyRegistryAddress;

    event ProxyRegistryAddressChanged(address indexed _newAddress);

    constructor(
        string memory uri_
    ) ERC1155(uri_) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    /**
     * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-less listings.
     */
    function isApprovedForAll(address _owner, address _operator)
        override
        public
        view
        returns (bool)
    {
        // Whitelist OpenSea proxy contract for easy trading.
        TradableProxyRegistry proxyRegistry = TradableProxyRegistry(proxyRegistryAddress);
        if (proxyRegistry.whitelistedOperators(_operator)) {
            return true;
        }

        return super.isApprovedForAll(_owner, _operator);
    }

    function setProxyRegistryAddress(address _proxyRegistryAddress) public onlyRole(DEFAULT_ADMIN_ROLE) {
        proxyRegistryAddress = _proxyRegistryAddress;
        emit ProxyRegistryAddressChanged(_proxyRegistryAddress);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, AccessControlEnumerable) returns (bool) {
        return interfaceId == type(ERC1155Supply).interfaceId
         || interfaceId == type(IAccessControlEnumerable).interfaceId
         || super.supportsInterface(interfaceId);
    }

    function owner() public view returns (address) {
        return getRoleMember(DEFAULT_ADMIN_ROLE, 0);
    }

    function _msgSender()
        internal
        override
        view
        returns (address sender)
    {
        return msgSender();
    }
}

contracts/lands/LandClaimer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./LandERC721.sol";
import "../mixins/ContextMixin.sol";

contract LandClaimer is Ownable, ContextMixin {

    address public landContractAddress;
    address public signerAddress;

    using ECDSA for bytes32; 

    constructor(address _landAddress, address _signerAddress) {
        landContractAddress = _landAddress;
        signerAddress = _signerAddress;
    }

    function mint(LandERC721.Properties calldata _properties, bytes calldata _signature) public returns (uint256) {

        bytes memory props = abi.encodePacked(
            _properties.x,
            _properties.y,
            _properties.climate,
            _properties.landType,
            _properties.resource1,
            _properties.resource2,
            _properties.resource3,
            _properties.resource4,
            _properties.resourceLevel1,
            _properties.resourceLevel2,
            _properties.resourceLevel3,
            _properties.resourceLevel4
        );

        bytes32 _hash = keccak256(props);

        address _signer = _hash.toEthSignedMessageHash().recover(_signature);

        require(_signer == signerAddress, "Signature invalid.");

        LandERC721 _land = LandERC721(landContractAddress);

        return _land.mint(msgSender(), _properties);
    }
}

