contracts/laws/CoinageLaws.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../GubbinsERC20.sol";
import "../resources/ResourcesERC1155.sol";

/**
 * @dev Contract is responsable of minting and burning our Penny tokens.
 * 
 * For minting, it takes gold bar from user and gives penny tokens.
 * For burning, it takes pennies and gives back gold bars.
 * 
 * Minting and burning will collect fees too.
 */
contract CoinageLaws is Ownable {
    // who collects fees for minting and burning coins (governance / kingdom treasury)
    address public feeCollector;
    // erc-20 token (gubbins token)
    GubbinsERC20 public token;
    // erc-1155 token (resources)
    ResourcesERC1155 public resources;
    // resource token id (gold bar)
    uint256 public resourceId = 0;
    // how much one gold bar is worth in pennies.
    // in medieval times gold coin weighted ~3.5g, so lets say our gold bar is 1kg, which makes allows us to mint 285 coins.
    //   should be dynamic and adjusted by how much gold bars and pennies are in circulation?
    uint256 public goldBarInPennies = 285 ether;
    // minimum amount paid by user to mint / burn coins
    uint256 public fixedMintingFee = 2 ether;
    uint256 public fixedBurningFee = 2 ether;
    // dynamic fee paid from minted / burned coins (10_000 = 100%, 100 = 1%, 50 = 0.5%)
    uint16 public dynamicMintingFee = 50;
    uint16 public dynamicBurningFee = 50;

    constructor(address _token, address _resources, uint256 _tokenId) {
        token = GubbinsERC20(_token);
        resources = ResourcesERC1155(_resources);
        resourceId = _tokenId;
    }
}

contracts/resources/ResourcesERC1155.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../mixins/ERC1155TradableMixin.sol";


contract ResourcesERC1155 is ERC1155TradableMixin {

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");

    struct Metadata {
        string name;
        uint8 itemType;
    }

    event ResourceRegistered(uint256 _id, Metadata _metadata);

    uint256 public tokenCount;
    mapping(uint256 => Metadata) public items;
    mapping(uint256 => bool) public registeredItems;

    string private __contractURI;

    constructor(string memory _uri, string memory _initContractURI) ERC1155TradableMixin(_uri) {
        __contractURI = _initContractURI;
    }

    function create(Metadata calldata _metadata) public onlyRole(CREATOR_ROLE) returns (uint256) {
        uint256 id = tokenCount;
        tokenCount = tokenCount + 1;

        items[id] = _metadata;
        registeredItems[id] = true;
        _mint(_msgSender(), id, 0, "");

        emit ResourceRegistered(id, _metadata);

        return id;
    }

    function mint(
        address to,
        uint256 id,
        uint256 amount
    ) public onlyRole(MINTER_ROLE) {
        require(registeredItems[id], "resource is not registered.");
        _mint(to, id, amount, "");
    }

    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public onlyRole(BURNER_ROLE) {
        _burn(account, id, value);
    }

    function contractURI() public view returns (string memory) {
        return __contractURI;
    }

    function setContractURI(string memory uri) public onlyRole(DEFAULT_ADMIN_ROLE) {
        __contractURI = uri;
    }
}

contracts/buildings/CoinEngraver.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../laws/CoinageLaws.sol";

/**
 * @dev Contract is responsable of minting and burning our Penny tokens.
 * 
 * For minting, it takes gold bar from user and gives penny tokens.
 * For burning, it takes pennies and gives back gold bars.
 * 
 * Minting and burning will collect fees too.
 */
contract CoinEngraver is Ownable {

    using SafeMath for uint256;

    CoinageLaws public coinageLaws;

    /**
     * @dev send {_sentAmount} of gold bars and receives {amount} of pennies.
     * {amount} returned already excludes {fee} amount which will be sent to kingdoms treasury.
     */
    function getExpectedMintReturn(uint256 _sentAmount) public view returns (uint256 amount, uint256 fee) {
        amount = _sentAmount.mul(coinageLaws.goldBarInPennies());
        fee = amount.mul(coinageLaws.dynamicMintingFee() / 10_000).add(coinageLaws.fixedMintingFee());
        if (fee > 0) {
            amount = amount.sub(fee);
        }
    }

    /**
     * @dev send {_sentAmount} of pennies and receive {amount} gold bars.
     * {fee} of pennies will be deducted from {_sentAmount} before they are being converted to gold bars.
     */
    function getExpectedBurnReturn(uint256 _sentAmount) public view returns (uint256 amount, uint256 fee) {
        fee = _sentAmount.mul(coinageLaws.dynamicBurningFee() / 10_000).add(coinageLaws.fixedBurningFee());

        if (fee >= _sentAmount) {
            amount = 0;
        } else {
            _sentAmount = _sentAmount.sub(fee);
            amount = _sentAmount.div(coinageLaws.goldBarInPennies());
        }
    }

    /**
     * @dev sent gold bars to receive golden pennies.
     */
    function mint(uint256 _sentAmount) public {
        ResourcesERC1155 res = coinageLaws.resources();
        require(res.balanceOf(_msgSender(), coinageLaws.resourceId()) >= _sentAmount, "coiner: no gold bars");

        (uint256 amountToMint, uint256 fee) = getExpectedMintReturn(_sentAmount);

        if (amountToMint > 0) {
            // burn gold bars from user inventory
            coinageLaws.resources().burn(_msgSender(), coinageLaws.resourceId(), _sentAmount);
            // send pennies to user
            coinageLaws.token().mint(_msgSender(), amountToMint);
        }

        if (fee > 0) {
            coinageLaws.token().mint(coinageLaws.feeCollector(), fee);
        }
    }

    /**
     * @dev send pennies to receive gold bars.
     */
    function burn(uint256 _sentAmount) public {
        GubbinsERC20 penny = coinageLaws.token();
        require(penny.balanceOf(_msgSender()) >= _sentAmount, "coiner: no gold bars");

        (uint256 amount, uint256 fee) = getExpectedBurnReturn(_sentAmount);

        if (amount > 0) {
            // send burning fee to treasury
            penny.transferFrom(_msgSender(), coinageLaws.feeCollector(), fee);
            // burn tokens user sent
            penny.burnFrom(_msgSender(), _sentAmount.sub(fee));
            // send gold bars to user
            coinageLaws.resources().mint(_msgSender(), coinageLaws.resourceId(), amount);
        }
    }
}

